mapper.select:
/* LoginMapper - selectMemberById */
SELECT USER_ID
     , PASSWORD
     , NAME
  FROM MEMBERS
 WHERE USER_ID 	= #{USER_ID}

mapper.insert:
	/* LoginMapper - insertMember */
	INSERT INTO MEMBERS ( 
	       USER_ID
	     , PASSWORD
   	     , NAME
	     ) VALUES (
		   #{USER_ID}
		 , #{PASSWORD} 
		 , #{NAME}
    )

mapper.delete:
/* LoginMapper - deleteMember */
DELETE MEMBERS
 WHERE USER_ID 	= #{USER_ID}
